import React from 'react';
import TaskItem from './TaskItem';

const EmployeeCard = ({ employee, filteredTasks }) => {
  const tasksToShow = filteredTasks.filter(task => 
    employee.tasks.some(empTask => empTask.id === task.id)
  );

  return (
    <div className="employee-card">
      <div className="employee-header">
        <h3 className="employee-name">{employee.name}</h3>
        <p className="employee-role">{employee.role}</p>
      </div>
      
      <div className="tasks-section">
        <div className="tasks-header">
          <h4 className="tasks-title">Tasks</h4>
          <span className="task-count">{tasksToShow.length}</span>
        </div>
        
        <div className="tasks-list">
          {tasksToShow.length > 0 ? (
            tasksToShow.map(task => (
              <TaskItem key={task.id} task={task} />
            ))
          ) : (
            <div className="no-tasks">No tasks match the current filter</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EmployeeCard;
