import React, { useState, useEffect } from 'react';
import Dashboard from './components/Dashboard';
import EmployeeCard from './components/EmployeeCard';
import AddTaskModal from './components/AddTaskModal';
import mockData from './data/mockData.json';

function App() {
  const [employees, setEmployees] = useState([]);
  const [statusFilter, setStatusFilter] = useState('All');
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Load data on component mount
  useEffect(() => {
    // Try to load from localStorage first, fallback to mock data
    const savedData = localStorage.getItem('employeeTaskData');
    if (savedData) {
      setEmployees(JSON.parse(savedData));
    } else {
      setEmployees(mockData.employees);
    }
  }, []);

  // Save to localStorage whenever employees data changes
  useEffect(() => {
    if (employees.length > 0) {
      localStorage.setItem('employeeTaskData', JSON.stringify(employees));
    }
  }, [employees]);

  // Get all tasks and apply filter
  const getAllTasks = () => {
    return employees.flatMap(emp => emp.tasks);
  };

  const getFilteredTasks = () => {
    const allTasks = getAllTasks();
    if (statusFilter === 'All') {
      return allTasks;
    }
    return allTasks.filter(task => task.status === statusFilter);
  };

  const handleAddTask = (employeeId, newTask) => {
    setEmployees(prevEmployees => 
      prevEmployees.map(emp => 
        emp.id === employeeId 
          ? { ...emp, tasks: [...emp.tasks, newTask] }
          : emp
      )
    );
  };

  const filteredTasks = getFilteredTasks();

  return (
    <div className="App">
      <header className="header">
        <div className="container">
          <h1>Employee Task Tracker</h1>
          <p>Manage tasks and track team progress</p>
        </div>
      </header>

      <main className="container">
        {/* Dashboard Section */}
        <Dashboard employees={employees} />

        {/* Controls Section */}
        <div className="controls">
          <div className="filter-group">
            <label htmlFor="status-filter">Filter by Status:</label>
            <select
              id="status-filter"
              className="filter-select"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="All">All Tasks</option>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>
          
          <button 
            className="add-task-btn"
            onClick={() => setIsModalOpen(true)}
          >
            + Add New Task
          </button>
        </div>

        {/* Employees Grid */}
        <div className="employees-grid">
          {employees.map(employee => (
            <EmployeeCard
              key={employee.id}
              employee={employee}
              filteredTasks={filteredTasks}
            />
          ))}
        </div>
      </main>

      {/* Add Task Modal */}
      <AddTaskModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        employees={employees}
        onAddTask={handleAddTask}
      />
    </div>
  );
}

export default App;
